	public static String getCtrls(int address){
		ByteArrayOutputStream outputStream = new ByteArrayOutputStream( );

		if(address >= ROM1.length){
			outputStream.write(0);
		}else
			outputStream.write(ROM1[address]);
		
		if(address >= ROM2.length){
			outputStream.write(0);
		}else
			outputStream.write(ROM2[address]);
				
		if(address >= ROM3.length){
			outputStream.write(0);
		}else
			outputStream.write(ROM3[address]);
		

		byte c[] = outputStream.toByteArray( );
		
		String buff = "";
		for (byte b : c) {
			buff = String.format("%8s", Integer.toBinaryString(b)).replace(" ", "0") + buff;
		}
		return buff;
	}

	public static void output(int addr){
		DataBus.write((byte) ROM1[addr]);
	}

